// Lukup streaming content
// (c) Kallol Borah, 2022

//"SPDX-License-Identifier: MIT"

pragma solidity 0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";

import "./IContent.sol";

contract Content is IContent, ERC721URIStorage, Ownable, Pausable {

    using SafeMath for uint256;

    using Counters for Counters.Counter;
    Counters.Counter private _tokenIds;
    
    uint256 private term;

    // Tokens (eg, USDC, DAI, Ether, etc) that are staked by creators, advertisers and verifiers
    address[] private StakingTokens;

    // Mapping of staked tokens to their index
    mapping(address => uint256) private StakingTokensIndex;

    // Liquidity providers that stake tokens
    address[] private LPs;

    // Mapping of liquidity providers to their index
    mapping(address => uint256) private LPIndex;
    
    // All content token ids
    uint256[] private allTokens;

    // Mapping from content token id to position in the allTokens array
    mapping(uint256 => uint256) private allTokensIndex;

    // Mapping from owner to list of owned content token IDs to content ID added
    mapping(address => mapping(uint256 => uint256)) private ownedTokens;

    // Mapping from content token ID to its position in the index of owned content ids
    mapping(uint256 => uint256) private ownedTokensIndex;

    // Mapping content ids to views, shares, and purchases of content
    mapping(uint256 => Item) private content;

    // Mapping content category to content ids in it
    mapping(bytes32 => uint256[]) private categories;

    // Mapping content ids to content type (eg, PPV, FREE) to its price (if its PPV) in Lukup tokens
    mapping(uint256 => mapping(bytes32 => uint256)) private pricing;

    // Mapping content creators and advertisers to staked tokens
    mapping(address => mapping(string => Stake)) private creators;

    // Mapping stakers to staked tokens to stakes
    mapping(address => mapping(string => uint256)) private stakes;

    // Mapping content ids to key shards
    mapping(uint256 => string[]) private keys;

    // Mapping content ids to quorum
    mapping(uint256 => uint256) private quorum;

    // Mapping content ids to subscribers to time of subscription
    mapping(uint256 => mapping(address => uint256)) private subscriptions;

    // Mapping users to revenue category to earnings
    mapping(address => mapping(bytes32 => uint256)) private categoryEarnings;

    // Mapping users to content id to earnings
    mapping(address => mapping(uint256 => uint256)) private itemEarnings;

    // Mapping advertisers to ad id to expenses
    mapping(address => mapping(uint256 => uint256)) private adExpenses;

    // Mapping users to content ids to other users they share it with
    mapping(address => mapping(uint256 => mapping(address => uint256))) private shares;

    // Mapping advertisers to ad ids to content ids that ads display on
    mapping(address => mapping(uint256 => uint256[])) private adViews;

    constructor () ERC721 ("Lukup Stream", "LUKUP"){
    }

    function createContent( string memory tokenURI, 
                            bytes32 pricingModel, 
                            uint256 price, 
                            string memory stakedToken, 
                            uint256 staked,
                            string[] memory shards,
                            uint256 keyquorum) override external returns (uint256) {
        require(creators[msg.sender][stakedToken].tokenAmount >= staked);
        _tokenIds.increment();
        uint256 newItemId = _tokenIds.current();
        _mint(msg.sender, newItemId);
        _setTokenURI(newItemId, tokenURI);
        categories[pricingModel].push(newItemId);
        allTokensIndex[newItemId] = allTokens.length;
        allTokens.push(newItemId);
        ownedTokens[msg.sender][ERC721.balanceOf(msg.sender)] = newItemId;
        ownedTokensIndex[newItemId] = ERC721.balanceOf(msg.sender);
        pricing[newItemId][pricingModel] = price;
        stakes[msg.sender][stakedToken] = staked;
        keys[newItemId] = shards;
        quorum[newItemId] = keyquorum;
        SafeMath.sub(creators[msg.sender][stakedToken].tokenAmount, staked);
        return newItemId;
    }

    function tokenOfOwnerByIndex(address owner, uint256 index) external view override returns (uint256) {
        require(index < ERC721.balanceOf(owner), "owner index out of bounds");
        return ownedTokens[owner][index];
    }

    function totalSupply() external view override returns (uint256) {
        return allTokens.length;
    }

    function tokenByIndex(uint256 index) external view override returns (uint256) {
        require(index < allTokens.length, "token index out of bounds");
        return allTokens[index];
    }

    function removeContent(uint256 contentId, bytes32 category) override external {
        require(ownerOf(contentId)==msg.sender);
        _burn(contentId);       
        delete keys[contentId];
        delete quorum[contentId];
        delete allTokensIndex[contentId];
        delete ownedTokens[msg.sender][ownedTokensIndex[contentId]];
        delete categories[category][contentId];
    }

    function supportTokens(address _token) override external {
        require(msg.sender==owner());
        StakingTokensIndex[_token] = StakingTokens.length;
        StakingTokens.push(_token);
    }

    function checkSupportForToken(address _token) override external view returns(bool){
        if(StakingTokensIndex[_token] > 0 && StakingTokens.length > 0){
            return true;
        }
        else
            return false;
    }

    receive() external payable{
        //ether paid in
        require(msg.value !=0);
        //contract must not be paused
        require(paused() == false);
        creators[msg.sender]["ether"].tokenAmount = SafeMath.add(creators[msg.sender]["ether"].tokenAmount, msg.value);
        bool found = false;
        if(LPIndex[msg.sender] >0 && LPs.length > 0)
            found = true;
        if(!found){
            LPIndex[msg.sender] = LPs.length;
            LPs.push(msg.sender);
        }
    }

    function stake(address _token, uint256 _amount) override external {
        require(paused() == false);
        bool found = false;
        if(StakingTokensIndex[_token] > 0 && StakingTokens.length > 0)
            found = true;
        if(found){
            if(IERC20(_token).transferFrom(msg.sender, address(this), _amount)){
                creators[msg.sender][ERC20(_token).name()].tokenAmount = SafeMath.add(creators[msg.sender][ERC20(_token).name()].tokenAmount, _amount);
                creators[msg.sender][ERC20(_token).name()].tokenAddress = _token;
                found = false;
                if(LPIndex[msg.sender] >0 && LPs.length > 0)
                    found = true;
                if(!found){
                    LPIndex[msg.sender] = LPs.length;
                    LPs.push(msg.sender);
                }
            }
        }
    }

    function fetchContentByCategory(bytes32 category) override external view returns(uint256[] memory){
        return categories[category];
    }

    function subscribe(uint256 contentId) override external {
        if(stakes[msg.sender]["VITTA"] > pricing[contentId]["PPV"]){
            subscriptions[contentId][msg.sender] = block.timestamp;
            content[contentId].bought = SafeMath.add(content[contentId].bought, 1);
            categoryEarnings[ownerOf(contentId)]["PPV"] = SafeMath.add(categoryEarnings[ownerOf(contentId)]["PPV"], pricing[contentId]["PPV"]);
            itemEarnings[ownerOf(contentId)][contentId] = SafeMath.add(itemEarnings[ownerOf(contentId)][contentId], pricing[contentId]["PPV"]);
        }
    }

    function viewContent(uint256 contentId) override external returns(string[] memory){
        if(pricing[contentId]["PPV"]>0){
            if(block.timestamp > SafeMath.add(subscriptions[contentId][msg.sender], term)){
                content[contentId].views = SafeMath.add(content[contentId].views, 1);
                return (keys[contentId]);
            }
        }
        else if(pricing[contentId]["FREE"]==0){
            content[contentId].views = SafeMath.add(content[contentId].views, 1);
            return (keys[contentId]);
        }
        return (new string[](1));
    }

    function viewAd(uint256 adId, uint256 contentId) override external returns(string[] memory){
        if(pricing[adId]["AD"]>0 && pricing[contentId]["FREE"]==0){
            if(block.timestamp > SafeMath.add(subscriptions[contentId][msg.sender], term)){
                content[adId].views = SafeMath.add(content[adId].views, 1);
                categoryEarnings[ownerOf(contentId)]["FREE"] = SafeMath.add(categoryEarnings[ownerOf(contentId)]["FREE"], pricing[adId]["AD"]);
                itemEarnings[ownerOf(contentId)][contentId] = SafeMath.add(itemEarnings[ownerOf(contentId)][contentId], pricing[adId]["AD"]);
                adExpenses[ownerOf(adId)][adId] = SafeMath.add(adExpenses[ownerOf(adId)][adId], pricing[adId]["AD"]);
                adViews[ownerOf(adId)][adId].push(contentId);
                return (keys[adId]);
            }
        }
        return (new string[](1));
    }

    function shareContent(address sharedWith, uint256 contentId) override external {
        if(shares[msg.sender][contentId][sharedWith]==0){
            shares[msg.sender][contentId][sharedWith]=1;
            content[contentId].shares = SafeMath.add(content[contentId].shares, 1);
        }
    }

    function fetchEarningsbyCategory(bytes32 category) override external view returns(uint256){
        return (categoryEarnings[msg.sender][category]);
    } 

    function fetchEarningsForItem(uint256 contentId) override external view returns(uint256){
        return (itemEarnings[msg.sender][contentId]);
    }

    function fetchExpensesForAd(uint256 adId) override external view returns(uint256){
        return (adExpenses[msg.sender][adId]);
    }

    function viewPerformance(uint256 contentId) override external view returns(uint256, uint256, uint256){
        return (content[contentId].views, content[contentId].shares, content[contentId].bought);
    }

    function viewDelivery(uint256 adId) override external view returns(uint256[] memory){
        return (adViews[msg.sender][adId]);
    }

    function setLicenseTerm(uint256 time) override external{
        require(msg.sender == owner());
        term = time;
    }

    function pause() public {
        require(msg.sender == owner());
        _pause();
    }
    
    function unpause() public {
        require(msg.sender == owner());
        _unpause();
    }

}
