pragma solidity 0.8.0;

interface IContent {

    struct Item{
        uint256 views;
        uint256 shares;
        uint256 bought;        
    }

    struct Stake{
        uint256 tokenAmount;
        address tokenAddress;
    }

    function createContent(string memory tokenURI, bytes32 pricingModel, uint256 price, string memory stakedToken, uint256 staked, string[] memory shards, uint256 keyquorum) external returns (uint256);

    function tokenOfOwnerByIndex(address owner, uint256 index) external view returns (uint256);

    function totalSupply() external view returns (uint256);

    function tokenByIndex(uint256 index) external view returns (uint256);
    
    function removeContent(uint256 contentId, bytes32 category) external;
    
    function supportTokens(address _token) external;

    function checkSupportForToken(address _token) external view returns(bool);

    function stake(address _token, uint256 _amount) external;

    function viewPerformance(uint256 contentId) external view returns(uint256, uint256, uint256);

    function viewDelivery(uint256 adId) external view returns(uint256[] memory);

    function fetchContentByCategory(bytes32 category) external view returns(uint256[] memory);

    function subscribe(uint256 contentId) external;

    function viewContent(uint256 contentId) external returns(string[] memory);

    function viewAd(uint256 adId, uint256 contentId) external returns(string[] memory);

    function shareContent(address sharedWith, uint256 contentId) external;

    function setLicenseTerm(uint256 time) external;

    function fetchEarningsbyCategory(bytes32 category) external view returns(uint256);

    function fetchEarningsForItem(uint256 contentId) external view returns(uint256);

    function fetchExpensesForAd(uint256 adId) external view returns(uint256);

}
